function handler(event) {
  var request = event.request;
  var uri = request.uri;

  // Check if this is the root path and redirect to /app
  if (uri === '/' || uri === '') {
    return {
      statusCode: 302,
      statusDescription: 'Found',
      headers: {
        'location': { value: '/app' }
      }
    };
  }

  // Strip /app prefix from the URI for /app paths
  if (uri === '/app' || uri === '/app/') {
    request.uri = '/';
  } else if (uri.startsWith('/app/')) {
    request.uri = uri.slice(4); // Remove /app prefix
  }

  return request;
}

const response = require("cfn-response");

function handler(event, context) {
    try {
        const originalEvent = JSON.parse(event.Records[0].body);
        const responseBody = {
            message: "Custom resource lambda failed to to process the event",
            originalEvent,
        };
        response.send(originalEvent, context, response.FAILED, responseBody);
    } catch (e) {
        console.error(e);
    }
}

exports.handler = handler;

const response = require('cfn-response');
const { S3 } = require('aws-sdk');
exports.handler = function (event, context) {
    const bucket = event.ResourceProperties.s3Bucket;
    if (event.RequestType === 'Delete') {
        purgeOutputBucket(bucket).then(() => {
            response.send(event, context, response.SUCCESS, {});
        }).catch((err) => {
            console.log('ERR', err);
            response.send(event, context, response.FAILED, { err });
        });
    } else {
        response.send(event, context, response.SUCCESS, {});
    }
};
async function purgeOutputBucket(bucket) {
    let objects = null;
    try {
        objects = await new S3().listObjects({
            Bucket: bucket
        }).promise();
    } catch (e) {
        if (e.code !== 'NoSuchBucket') {
            throw e;
        }
    }
    if (objects) {
        if (objects.Contents.length === 0) {
            return;
        }
        const keys = objects.Contents.map(c => ({ Key: c.Key }));
        const deleteRequest = {
            Bucket: bucket,
            Delete: {
                Objects: keys
            }
        };
        await new S3().deleteObjects(deleteRequest).promise();
        if (objects.IsTruncated) {
            await purgeOutputBucket(bucket);
        }
    }
